mapper.select:
select sum(payment)
from (
  select sales.payment, sales_id
  from sales
  where customer_user_id = #{customerUserId, jdbcType=INTEGER}
  and  #{startDate,jdbcType=DATE} < sales_time
  and #{endDate,jdbcType=DATE} > sales_time
  ) s;

select *
from (select g.* , sum(s.payment) pm from sales s
join goods_in_warehouse giw on s.goods_in_warehouse_idgoods_in_warehouse = giw.idgoods_in_warehouse
join goods g on giw.goods_goods_id = g.goods_id
 where warehouse_warehouse_id= #{warehouseId} 
group by goods_goods_id) s
order by s.pm DESC;
limit #{itemsPerPage} offset #{pageIndex}

select *
from (select g.* , sum(s.amount) am from sales s
         join goods_in_warehouse giw on s.goods_in_warehouse_idgoods_in_warehouse = giw.idgoods_in_warehouse
         join goods g on giw.goods_goods_id = g.goods_id
          where warehouse_warehouse_id= #{warehouseId} 
        group by goods_goods_id) s
order by s.am DESC;
limit #{itemsPerPage} offset #{pageIndex}

select

from sales
where sales_id is not null

  and customer_user_id = #{customerUserId,jdbcType=INTEGER}


  and amount = #{amount,jdbcType=INTEGER}


  and payment = #{payment,jdbcType=DECIMAL}


  and is_paid = #{isPaid,jdbcType=CHAR}


  and order_order_id = #{orderOrderId,jdbcType=INTEGER}


  and goods_in_warehouse_idgoods_in_warehouse = #{goodsInWarehouseId, jdbcType=INTEGER}


  and sales_time = #{salesTime, jdbcType=DATE};
